const clickMan = document.querySelector('.last-man');
const clickWoman = document.querySelector('.first-woman');
const collMan = document.querySelector('.man');
const collWoman = document.querySelector('.woman');
clickMan.addEventListener('click', function(event){
    clickMan.classList.add('click--active');
    clickWoman.classList.add('click-woman--active');
    collMan.classList.add('man--active');
    collWoman.classList.add('woman--active');
});
clickWoman.addEventListener('click', function(event){
    collMan.classList.remove('man--active');
    collWoman.classList.remove('woman--active');
    clickMan.classList.remove('click--active');
    clickWoman.classList.remove('click-woman--active');
});
